import React, {useState} from 'react';
import Button from '../../components/Button/index';
import style from './index.module.scss';

const Filter = ({onClickHandler}) => {
    const [active,setActive] = useState(false);
    useEffect(() => {
        console.log(selected);
    },[])
    const data = [
        {
            id:1,
            heading: 'Launch Year',
            elements:[
                {id:1,text:'2006'},
                {id:2,text:'2007'},
                {id:3,text:'2008'},
                {id:4,text:'2009'},
                {id:5,text:'2010'},
                {id:6,text:'2011'},
                {id:7,text:'2012'},
                {id:8,text:'2013'},
                {id:9,text:'2014'},
                {id:10,text:'2015'},
                {id:11,text:'2016'},
                {id:12,text:'2017'},
                {id:13,text:'2018'},
                {id:14,text:'2019'},
                {id:15,text:'2020'}
            ]
        },
        {
            id:2,
            heading: 'Successful Launch',
            elements:[
                {id:1,text:'true'},
                {id:2,text:'false'}
            ]
        },
        {
            id:3,
            heading: 'Successful Landing',
            elements:[
                {id:1,text:'true'},
                {id:2,text:'false'}
            ]
        }
    ]
    return (
        <div className={style.filterContainer}>
            <div className={style.filterHeading}>Filters</div>
            {data && data.map(el => {
                return (
                    <>
                    <div className={style.headingWrapper}>
                        <p className={style.heading}>{el.heading}</p>
                    </div>
                    <div className={style.filterWrapper}>
                    {el.elements && el.elements.map(element => {
                        return (
                            <div className={style.filterButtonWrapper}>
                            <Button text={element.text} onClickHandler={(e)=>onClickHandler(e,el.heading)}/>
                            </div>
                        )
                    })}
                    </div>
                    </>
                )
            })}
        </div>
    )
}

export default Filter;
